import React, { useState } from "react";
// import { FaUserCircle } from "react-icons/fa";
import { TiDeleteOutline } from "react-icons/ti";
import { useAuth } from "../context/AuthContext";
import { LuLogOut } from "react-icons/lu";
import logo from "../../assets/images/logo.svg";

const Header = () => {
  const { register, signInWithGoogle, LogIn, user, LogOut } = useAuth();
  const [modal, setModal] = useState(false);
  const [userEmail, setUserEmail] = useState("");
  const [userPass, setUserPass] = useState("");
  const [error, setError] = useState("");

  async function handleRegister() {
    try {
      await register(userEmail, userPass);
      setUserEmail("");
      setUserPass("");
      setModal(false);
    } catch (err) {
      setError(err.message);
    }
  }

  async function handleGoogle() {
    try {
      setModal(false);
      return await signInWithGoogle();
     
    } catch (err) {
      setError(err.message);
    }
  }

  async function handleLogIn() {
    try {
      await LogIn(userEmail, userPass);
      setUserEmail("");
      setUserPass("");
      setModal(false);
    } catch (err) {
      setError(err.message);
    }
  }
  return (
    <div className="py-[30px] ">
      <div className="conteiner">
        <div className="flex items-center justify-between ">
          <img src={logo} alt="img" />

          <div className="flex items-center justify-between gap-[60px] relative">
            {user ? null : (
              <button
                onClick={() => setModal(true)}
                className="py-[10px] px-[32px] text-xl bg-red-700 text-white rounded-xl "
              >
                Sign in
              </button>
            )}
            {user ? (
              <div className="flex items-center flex-col">
                <img
                  src={
                    user
                      ? user.photoURL
                        ? user.photoURL
                        : `data:image/png;base64,`
                      : `data:image/png;base64,`
                  }
                  alt="img"
                  className=" sm:w-[30px]   rounded-[50%]"
                />
                <h1 className="sm:text-sm  xl:text-xl">
                  {user
                    ? user.displayName
                      ? user.displayName
                      : user.email
                    : "User"}
                </h1>
              </div>
            ) : null}

            {!user ? null : (
              <a
                onClick={() => LogOut()}
                className=" absolute top-[-10px] right-[-120px] text-3xl text-red-600 font-bold"
              >
                <LuLogOut />{" "}
              </a>
            )}
          </div>

          {modal ? (
            <div className="">
              <div className="fixed top-[150px] left-[37%]  w-[480px] h-[800px] z-50 bg-[#0f0e0ed8] px-[50px] flex items-start  justify-center flex-col gap-[30px]">
                <a
                  onClick={() => setModal(false)}
                  className="absolute top-[20px] right-5 text-3xl"
                >
                  {" "}
                  <TiDeleteOutline />{" "}
                </a>
                <h1 className="text-3xl font-bold">Войти</h1>

                <div class="relative z-0 w-full mb-5 group">
                  <input
                    onChange={(e) => setUserEmail(e.target.value)}
                    value={userEmail}
                    type="email"
                    name="floating_email"
                    id="floating_email"
                    placeholder="Email address...."
                    className="bg-[#161616B2] py-[18px] px-[32px] w-full text-white text-xl rounded-md border-2 border-gray-500 "
                  />
                </div>

                <div class="relative z-0 w-full mb-5 group">
                  <input
                    onChange={(e) => setUserPass(e.target.value)}
                    value={userPass}
                    type="password"
                    name="floating_email"
                    id="floating_email"
                    placeholder="password...."
                    className="bg-[#161616B2] py-[18px] px-[32px] w-full text-white text-xl rounded-md border-2 border-gray-500 "
                  />
                </div>

                <div className="flex items-center flex-col gap-[15px] w-full">
                <button
                   onClick={handleRegister}
                    type="button"
                    className="w-full py-[10px] px-[32px] text-xl font-bold bg-[#E50914]  rounded-[5px]"
                  >
                  Register
                  </button>
                  <h1 className="text-gray-300 text-xl">ИЛИ</h1>
                  <button
                    onClick={handleLogIn}
                    type="button"
                    className="w-full py-[10px] px-[32px] text-xl font-bold bg-[#4e4c4cb2]"
                  >
                    Log In
                  </button>
               
                 
                  <button
                  onClick={handleGoogle}
                    type="button"
                    className="w-full py-[10px] px-[32px] text-xl font-bold bg-[#212020b2] rounded-[5px] flex items-center justify-center"
                  >
                     <svg
                      className="w-4 h-4 me-2"
                      aria-hidden="true"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="currentColor"
                      viewBox="0 0 18 19"
                    >
                      <path
                        fillRule="evenodd"
                        d="M8.842 18.083a8.8 8.8 0 0 1-8.65-8.948 8.841 8.841 0 0 1 8.8-8.652h.153a8.464 8.464 0 0 1 5.7 2.257l-2.193 2.038A5.27 5.27 0 0 0 9.09 3.4a5.882 5.882 0 0 0-.2 11.76h.124a5.091 5.091 0 0 0 5.248-4.057L14.3 11H9V8h8.34c.066.543.095 1.09.088 1.636-.086 5.053-3.463 8.449-8.4 8.449l-.186-.002Z"
                        clipRule="evenodd"
                      />
                    </svg>
                 Sign in with Google
              </button>
                </div>
                {error.length ? (
                  <div
                    class="flex items-center p-4 mb-4 text-xl  rounded-lg  text-[#E50914] w-full"
                    role="alert"
                  >
                    <svg
                      class="flex-shrink-0 inline w-4 h-4 me-3"
                      aria-hidden="true"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                    >
                      <path d="M10 .5a9.5 9.5 0 1 0 9.5 9.5A9.51 9.51 0 0 0 10 .5ZM9.5 4a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3ZM12 15H8a1 1 0 0 1 0-2h1v-3H8a1 1 0 0 1 0-2h2a1 1 0 0 1 1 1v4h1a1 1 0 0 1 0 2Z" />
                    </svg>
                    <span class="sr-only">Info</span>
                    <div>
                      <span class="font-medium">{error}</span>
                    </div>
                  </div>
                ) : null}
              </div>
              <div onClick={() => setModal(false)} className="bg"></div>
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
};

export default Header;
